import React, { useState } from 'react';
import './fight.css';

const Fight = () => {
  const [playerHealth, setPlayerHealth] = useState(100);
  const [enemyHealth, setEnemyHealth] = useState(100);
  const [battleLog, setBattleLog] = useState([]);

  const playerCards = [
    { id: 1, name: "화염구", damage: 20, element: "fire" },
    { id: 2, name: "얼음화살", damage: 15, element: "ice" },
    { id: 3, name: "번개주먹", damage: 25, element: "lightning" },
  ];

  const attack = (card) => {
    const damage = card.damage;
    setEnemyHealth(prevHealth => Math.max(prevHealth - damage, 0));
    setBattleLog(prevLog => [...prevLog, `플레이어가 ${card.name}으로 ${damage}의 데미지를 입혔습니다!`]);

    // 적의 반격
    setTimeout(() => {
        const enemyDamage = Math.floor(Math.random() * 15) + 5;
        setPlayerHealth(prevHealth => Math.max(prevHealth - enemyDamage, 0));
        setBattleLog(prevLog => [...prevLog, `적이 ${enemyDamage}의 데미지를 입혔습니다!`]);
      }, 1000);};

  return (
    <div className="fight-container">
      <div className="health-bars">
        <div className="health-bar">
          <p>플레이어 체력: {playerHealth}</p>
          <div className="health-bar-inner" style={{width: `${playerHealth}%`}}></div>
        </div>
        <div className="health-bar">
          <p>적 체력: {enemyHealth}</p>
          <div className="health-bar-inner enemy" style={{width: `${enemyHealth}%`}}></div>
        </div>
      </div>

      <div className="battle-area">
        <div className="player-area">
          <img src="/placeholder.svg?height=200&width=150" alt="플레이어" className="character-image" />
        </div>
        <div className="enemy-area">
          <img src="/placeholder.svg?height=200&width=150" alt="적" className="character-image" />
        </div>
      </div>

      <div className="card-area">
        {playerCards.map(card => (
          <div key={card.id} className={`card ${card.element}`} onClick={() => attack(card)}>
            <h3>{card.name}</h3>
            <p>데미지: {card.damage}</p>
          </div>
        ))}
      </div>

      <div className="battle-log">
        <h3>전투 로그</h3>
        <ul>
          {battleLog.map((log, index) => (
            <li key={index}>{log}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Fight;
