import React, { useState } from 'react';
import './App.css';

function App() {
  const [isOpen, setIsOpen] = useState(false); // 로그인 창 상태 (초기에는 닫힘)

  const toggleLogin = () => {
    setIsOpen(!isOpen); // 버튼 클릭 시 상태 변경
  };

  return (
    <div className="App">
      {/* 배경 */}
      <div className="fullscreen-background"></div>

      {/* 열기 버튼 */}
      <button className="open-button" onClick={toggleLogin}>
        {isOpen ? '닫기' : '열기'}
      </button>

      {/* 로그인 창 */}
      {isOpen && (
        <div className="login-panel">
          <h2>로그인</h2>
          <input type="text" placeholder="아이디" />
          <input type="password" placeholder="비밀번호" />
          <button className="login-button">로그인</button>
        </div>
      )}
    </div>
  );
}

export default App;
