import React from 'react';
import { useNavigate } from 'react-router-dom';
import './MainPage.css';
import legendTestImage from './assets/images/legendtier6.png';

function MainPage({ currency }) {
  const navigate = useNavigate();

  const handleLogout = () => {
    // 로그아웃 로직
    navigate('/');
  };

  const handleStore = () => {
    // 상점으로 이동
    navigate('/store');
  };
  const handleDeck = () => {
    //내 덱으로 이동
    navigate('/deck')
  };

  return (
    <div className="main-container">
      {/* 사이드바 */}
      <div className="sidebar">
        <button className="menu-button" onClick={handleStore}>상점</button>
        <button className="menu-button">내카드</button>
        <button className="code-input">방 코드 입력</button>
        <button className="create-room">방 만들기</button>
      </div>

      {/* 메인 콘텐츠 */}
      <div className="main-content">
        <div className="header">
          <span className="money">현재 돈: {currency}원</span>
          <button className="logout-button" onClick={handleLogout}>로그아웃</button>
        </div>

        {/* 대표 몬스터 카드 */}
        <div className="monster-card">
          <img src={legendTestImage} alt="대표 몬스터 카드" className="monster-image" />
        </div>
      </div>
    </div>
  );
}

export default MainPage;
