import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Inventory.css';

function Inventory({ inventory, setInventory }) {
  const [showModal, setShowModal] = useState(false);
  const [openedCards, setOpenedCards] = useState([]);

  const openCardPack = (index) => {
    const cardPack = inventory[index];
    const newCards = drawCards(cardPack); // 카드팩에서 카드를 5장 뽑음
    setOpenedCards(newCards);
    setShowModal(true);

    setInventory(prevInventory => {
      const newInventory = [...prevInventory];
      newInventory.splice(index, 1); // 개봉한 카드팩 제거
      return newInventory;
    });
  };

  const drawCards = (cardPack) => {
    // 예시로 무작위 카드 5개를 뽑는 로직
    const drawnCards = [];
    for (let i = 0; i < 5; i++) {
      const randomCard = { 
        name: `Card ${Math.floor(Math.random() * 100)}`, // 임의의 카드 생성
        image: 'card-image-url', // 이미지 URL을 적절히 변경
      };
      drawnCards.push(randomCard);
    }
    return drawnCards;
  };

  const closeModal = () => {
    setShowModal(false);
    setOpenedCards([]);
  };

  return (
    <div className="inventory-page">
      <h2>내 인벤토리</h2>
      {inventory.length === 0 ? (
        <div className="inventory-empty">구매한 카드팩이 없습니다.</div>
      ) : (
        <div className="inventory-list">
          {inventory.map((card, index) => (
            <div key={index} className="inventory-item">
              <div className="card-pack">
                <img src={card.image} alt={card.name} className="card-pack-image" />
                <button className="open-button" onClick={() => openCardPack(index)}>
                  카드팩 개봉
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-card-message">
              새로운 카드가 나왔습니다!
            </div>
            <div className="modal-cards">
              {openedCards.map((card, index) => (
                <div key={index} className="modal-card">
                  <img src={card.image} alt={card.name} className="modal-card-image" />
                  <p>{card.name}</p>
                </div>
              ))}
            </div>
            <button className="close-modal" onClick={closeModal}>닫기</button>
          </div>
        </div>
      )}

      <Link to="/store" className="back-button">상점으로 돌아가기</Link>
    </div>
  );
}

export default Inventory;
