import React, { useState } from 'react';
import './App.css';

function App() {
  const [isOpen, setIsOpen] = useState(false); // 로그인 창 상태 (초기에는 닫힘)

  const toggleLogin = () => {
    setIsOpen(!isOpen); // 상태를 토글
  };

  return (
    <div className="App">
      {/* 풀스크린 배경 */}
      <div className="fullscreen-background"></div>

    {/* 우측 상단 로고 */}
    <img src={logo} alt="Logo" className="top-right-logo" />

      {/* 열기 버튼 (닫힌 상태일 때만 보임) */}
      {!isOpen && (
        <button className="toggle-button open" onClick={toggleLogin}>
          열기
        </button>
      )}

      {/* 로그인 창 */}
      <div className={`login-panel ${isOpen ? 'open' : ''}`}>
        <div className="login-content">
          <h2>로그인</h2>
          <input type="text" placeholder="아이디" />
          <input type="password" placeholder="비밀번호" />
          <button className="login-button">로그인</button>
        </div>
        {/* 닫기 버튼 (로그인창이 열렸을 때만 보임) */}
        {isOpen && (
          <button className="toggle-button close" onClick={toggleLogin}>
            닫기
          </button>
        )}
      </div>
    </div>
  );
}

export default App;
