import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import logo from './assets/images/logo.png';
import MainPage from './MainPage'; // MainPage 컴포넌트 추가
import { useNavigate } from 'react-router-dom'; // useNavigate 추가

function App() {
  const [isOpen, setIsOpen] = useState(false); // 로그인 창 상태 (초기에는 닫힘)
  const [isLoggedIn, setIsLoggedIn] = useState(false); // 로그인 여부

  // <Router> 안에서 navigate 훅 사용
  return (
    <Router>
      <div className="App">
        {/* 풀스크린 배경 */}
        <div className="fullscreen-background"></div>

        {/* 우측 상단 로고 */}
        <img src={logo} alt="Logo" className="top-right-logo" />

        {/* 열기 버튼 (닫힌 상태일 때만 보임) */}
        {!isOpen && (
          <button className="toggle-button open" onClick={() => setIsOpen(true)}>
            열기
          </button>
        )}

        {/* 로그인 창 */}
        <div className={`login-panel ${isOpen ? 'open' : ''}`}>
          <div className="login-content">
            <img src={logo} alt="Logo" className="login-logo" />
            <h2>로그인</h2>
            <input type="text" placeholder="아이디" />
            <input type="password" placeholder="비밀번호" />
            <button
              className="login-button"
              onClick={() => {
                setIsLoggedIn(true);
                useNavigate()('/main'); // MainPage로 이동
              }}
            >
              로그인
            </button>
            <button className="signup-button">회원가입</button>
          </div>

          {/* 닫기 버튼 (로그인창이 열렸을 때만 보임) */}
          {isOpen && (
            <button className="toggle-button close" onClick={() => setIsOpen(false)}>
              닫기
            </button>
          )}
        </div>

        {/* 라우팅 설정 */}
        <Routes>
          <Route path="/main" element={<MainPage />} />
          {/* 기본 경로 설정 (로그인 페이지) */}
          <Route path="/" element={<div>로그인 페이지</div>} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
