import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Switch, useHistory } from 'react-router-dom';
import logo from './assets/images/logo.png';
import MainPage from './MainPage'; // MainPage 컴포넌트 추가

function App() {
  const [isOpen, setIsOpen] = useState(false); // 로그인 창 상태 (초기에는 닫힘)
  const [isLoggedIn, setIsLoggedIn] = useState(false); // 로그인 여부
  const history = useHistory(); // history 객체를 사용하여 페이지 이동

  const toggleLogin = () => {
    setIsOpen(!isOpen); // 상태를 토글
  };

  const handleLogin = () => {
    // 로그인 로직 (임시로 바로 로그인 처리)
    setIsLoggedIn(true);
    history.push('/main'); // 로그인 후 MainPage로 이동
  };

  return (
    <Router>
      <div className="App">
        {/* 풀스크린 배경 */}
        <div className="fullscreen-background"></div>

        {/* 우측 상단 로고 */}
        <img src={logo} alt="Logo" className="top-right-logo" />

        {/* 열기 버튼 (닫힌 상태일 때만 보임) */}
        {!isOpen && (
          <button className="toggle-button open" onClick={toggleLogin}>
            열기
          </button>
        )}

        {/* 로그인 창 */}
        <div className={`login-panel ${isOpen ? 'open' : ''}`}>
          <div className="login-content">
            <img src={logo} alt="Logo" className="login-logo" />
            <h2>로그인</h2>
            <input type="text" placeholder="아이디" />
            <input type="password" placeholder="비밀번호" />
            <button className="login-button" onClick={handleLogin}>로그인</button>
            <button className="signup-button">회원가입</button>
          </div>

          {/* 닫기 버튼 (로그인창이 열렸을 때만 보임) */}
          {isOpen && (
            <button className="toggle-button close" onClick={toggleLogin}>
              닫기
            </button>
          )}
        </div>

        {/* 라우팅 설정 */}
        <Switch>
          <Route path="/main">
            <MainPage />
          </Route>
          {/* 기본 경로 설정 (로그인 페이지) */}
          <Route path="/">
            <div>로그인 페이지</div>
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
