import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import './Login.css';
import logo from './assets/images/logo.png';
import MainPage from './MainPage';
import SignUpPage from './SignUpPage';
import Inventory from './Inventory';
import StorePage from './StorePage';

function Login() {
  const [inventory, setInventory] = useState([]);
  const [currency, setCurrency] = useState(100);  // 재화 상태 관리

  const buyCardPack = (card) => {
    if (currency >= card.price) {
      setCurrency(currency - card.price);  // 재화 차감
      const newCardPack = { ...card, isOpened: false };
      setInventory((prevInventory) => [...prevInventory, newCardPack]);
      return true;
    }
    return false;
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LoginPanel />} />
        <Route path="/main" element={<MainPage currency={currency} />} />
        <Route path="/signup" element={<SignUpPage />} />
        <Route
          path="/store"
          element={<StorePage buyCardPack={buyCardPack} currency={currency} />}
        />
        <Route
          path="/inventory"
          element={<Inventory inventory={inventory} setInventory={setInventory} />}
        />
      </Routes>
    </Router>
  );
}

export default Login;
