import React from 'react';
import { useNavigate } from 'react-router-dom';
import './MainPage.css';
import legendTestImage from './assets/images/legendtier6.png';

function MainPage() {
  const navigate = useNavigate();
  const money = 100; // 예시로 현재 돈 값을 설정 (동적 데이터로 대체 가능)

  const handleLogout = () => {
    // 로그아웃 로직
    navigate('/');
  };

  const handleStore = () => {
    //상점 가는로직
    nevigate('/Store');
  }

  return (
    <div className="main-container">
      {/* 사이드바 */}
      <div className="sidebar">
        <button className="menu-button">상점</button>
        <button className="menu-button">내카드</button>
      </div>

      {/* 메인 콘텐츠 */}
      <div className="main-content">
        {/* 변경된 부분 */}
        <div className="header">
          <span className="money">현재 돈: {money}원</span>
          <button className="logout-button" onClick={handleLogout}>
            로그아웃
          </button>
        </div>

        {/* 대표 몬스터 카드 */}
        <div className="monster-card">
          <img src={legendTestImage} alt="대표 몬스터 카드" className="monster-image" />
        </div>

        {/* 하단 버튼 */}
        <div className="bottom-buttons">
          <button className="code-input">방 코드 입력</button>
          <button className="create-room">방만들기</button>
        </div>
      </div>
    </div>
  );
}

export default MainPage;
