import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, useHistory } from 'react-router-dom'; // React Router 관련 함수들
import './App.css'; // 스타일 파일 불러오기
import logo from './assets/images/logo.png'; // 로고 이미지 경로 지정

// 로그인 페이지 컴포넌트
function LoginPage() {
  const history = useHistory(); // 페이지 전환을 위한 history 객체
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // 로그인 성공 시 메인 페이지로 이동
    if (username && password) {
      history.push('/main'); // 로그인 성공 후 /main 경로로 이동
    }
  };

  return (
    <div className="login-page">
      <div className="login-content">
        <img src={logo} alt="Logo" className="login-logo" />
        <h2>로그인</h2>
        <input
          type="text"
          placeholder="아이디"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="비밀번호"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button className="login-button" onClick={handleLogin}>
          로그인
        </button>
      </div>
    </div>
  );
}

// 메인 페이지 컴포넌트
function MainPage() {
  return (
    <div className="main-page">
      <h1>메인 페이지</h1>
      <p>여기서는 메인 페이지 콘텐츠를 추가할 수 있습니다.</p>
    </div>
  );
}

// App 컴포넌트
function App() {
  return (
    <Router>
      <Switch>
        {/* 로그인 페이지 */}
        <Route path="/" exact component={LoginPage} />

        {/* 메인 페이지 */}
        <Route path="/main" component={MainPage} />
      </Switch>
    </Router>
  );
}

export default App;
