import React, { useState } from 'react';
import './App.css';
import logo from './assets/images/logo.png';

function App() {
  const [isOpen, setIsOpen] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const togglePanel = () => {
    setIsOpen(!isOpen);
  };

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    // 여기에 로그인 로직을 구현합니다
    console.log('로그인 시도:', username, password);
  };

  return (
    <div>
      <div className="fullscreen-background"></div>
      <img src={logo} alt="로고" className="top-right-logo" />

      <div className={`login-panel ${isOpen ? 'open' : ''}`}>
        <button className="toggle-button close" onClick={togglePanel} aria-label="로그인 패널 닫기">
          닫기
        </button>

        <div className="login-content">
          <img src={logo} alt="로고" className="login-logo" />

          <h2>로그인</h2>
          <form onSubmit={handleLogin}>
            <label htmlFor="username" className="sr-only">아이디</label>
            <input
              type="text"
              id="username"
              placeholder="아이디"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
            <label htmlFor="password" className="sr-only">비밀번호</label>
            <input
              type="password"
              id="password"
              placeholder="비밀번호"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <button type="submit" className="login-button">로그인</button>
          </form>
          <button className="signup-button">회원가입</button>
        </div>
      </div>

      {!isOpen && (
        <button className="toggle-button open" onClick={togglePanel} aria-label="로그인 패널 열기">
          열기
        </button>
      )}
    </div>
  );
}

export default App;
