import React, { useState } from 'react';
import './App.css';

function App() {
  const [isOpen, setIsOpen] = useState(false); // 창 상태 관리 (열림/닫힘)

  const toggleLoginBox = () => {
    setIsOpen(!isOpen); // 상태를 반전시켜 토글
  };

  return (
    <div className="fullscreen-background">
      {/* 왼쪽 로그인 창 */}
      <div className={`login-box ${isOpen ? 'open' : ''}`}>
        <h2>로그인</h2>
        <input type="text" placeholder="아이디" />
        <input type="password" placeholder="비밀번호" />
        <button>로그인</button>
      </div>

      {/* 토글 버튼 */}
      <button className="toggle-button" onClick={toggleLoginBox}>
        {isOpen ? '닫기' : '열기'}
      </button>
    </div>
  );
}

export default App;
