import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './MainPage.css';
import legendTestImage from './assets/images/legendtier6.png';
import StorePage from './StorePage';  // StorePage 임포트

function MainPage() {
  const navigate = useNavigate();
  const [currency, setCurrency] = useState(10011); // 현재 돈 상태

  const handleLogout = () => {
    navigate('/'); // 로그아웃 시 홈으로 이동
  };

  const handleStore = () => {
    navigate('/store');  // 상점으로 이동
  };

  // 카드팩 구매 함수 (currency 상태를 업데이트)
  const buyCardPack = (card) => {
    if (currency >= card.price) {
      setCurrency(currency - card.price);
      return true;
    } else {
      return false;
    }
  };

  return (
    <div className="main-container">
      <div className="sidebar">
        <button className="menu-button" onClick={handleStore}>상점</button>
        <button className="menu-button">내카드</button>
      </div>

      <div className="main-content">
        <div className="header">
          <span className="money">현재 돈: {currency}원</span>
          <button className="logout-button" onClick={handleLogout}>
            로그아웃
          </button>
        </div>

        <div className="monster-card">
          <img src={legendTestImage} alt="대표 몬스터 카드" className="monster-image" />
        </div>

        <div className="bottom-buttons">
          <button className="code-input">방 코드 입력</button>
          <button className="create-room">방 만들기</button>
        </div>
      </div>

      {/* StorePage로 currency와 buyCardPack을 전달 */}
      <StorePage buyCardPack={buyCardPack} currency={currency} />
    </div>
  );
}

export default MainPage;
