import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './StorePage.css';
import bCard from './assets/images/b_card.png';
import aCard from './assets/images/a_card.png';
import sCard from './assets/images/s_card.png';

// 카드팩 데이터와 확률
const availableCards = [
  { name: 'firetier1', type: 'fire', tier: 1, image: 'firetier1.png' },
  { name: 'firetier2', type: 'fire', tier: 2, image: 'firetier2.png' },
  // ... 다른 카드들
];

function StorePage({ buyCardPack, currency, addCardsToInventory }) {
  const navigate = useNavigate();

  const [cards] = useState([
    { image: bCard, name: "카드 B", price: 10, packImage: bCard },
    { image: sCard, name: "카드 S", price: 30, packImage: sCard },
    { image: aCard, name: "카드 A", price: 20, packImage: aCard },
  ]);

  const handleBuyCard = (index) => {
    const selectedCard = cards[index];
    if (currency >= selectedCard.price) {
      // 확률에 따라 카드를 뽑아서 인벤토리에 추가
      const drawnCards = drawCards(index);  // drawCards 함수에서 카드를 뽑음
      addCardsToInventory(drawnCards);  // 인벤토리 업데이트
      alert(`${selectedCard.name} 카드팩을 구매했습니다!`);
    } else {
      alert('재화가 부족합니다.');
    }
  };

  const drawCards = (index) => {
    const cardPack = cards[index];
    const drawnCards = [];

    // 카드팩별로 확률을 조정하여 카드를 뽑음
    for (let i = 0; i < 5; i++) {
      const randomCard = availableCards[Math.floor(Math.random() * availableCards.length)];
      drawnCards.push(randomCard);
    }

    return drawnCards;
  };

  return (
    <div className="store-container">
      <div className="header">
        <div className="currency">보유 재화: {currency} G</div>
        <div>
          <button className="inventory-button" onClick={() => navigate('/inventory')}>인벤토리</button>
          <button className="main-button" onClick={() => navigate('/main')}>메인으로 돌아가기</button>
        </div>
      </div>

      <h2>상점</h2>

      <div className="card-container">
        {cards.map((card, index) => (
          <div key={index} className="card">
            <img src={card.image} alt={`Card ${index + 1}`} className="card-image" />
            <p>{card.name} - {card.price} G</p>
            <button className="buy-button" onClick={() => handleBuyCard(index)}>구매하기</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default StorePage;
